$DAYS = 90; $CUTOFF = (Get-Date).ToUniversalTime().AddDays(-$DAYS).ToString("yyyy-MM-ddTHH:mm:ssZ"); az rest --method GET --url "https://graph.microsoft.com/v1.0/users?`$select=displayName,userPrincipalName,signInActivity,accountEnabled" --headers "ConsistencyLevel=eventual" --query "value[?(!signInActivity.lastSuccessfulSignInDateTime || signInActivity.lastSuccessfulSignInDateTime < '$CUTOFF')].{Name:displayName, UPN:userPrincipalName, Last:signInActivity.lastSuccessfulSignInDateTime}" --output table